package aug27eb;

public class DataTypeDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          
		int a = 10;
		char c = 'C';
		String s = "chintan Patel";
		float f = 777777777777.9999999999999999f;
		double d = 777777777777.9999999999999999;
		long l = 1234567895l;
		boolean b = true;
		
		System.out.println("Int value:"+a+"\nChar value:"+c+"\nString value:"
				+s+"\nfloat value:"+f+"\ndouble value:"+d+"\nlong value:"+l
				+"\n boolean value:"+b
				);
				
	}

}
